mapper.select:
SELECT DISTINCT b.* FROM blog b

    INNER JOIN blog_tag bt ON b.id = bt.blog_id


    b.status = 1

        AND b.category_id = #{categoryId}


        AND bt.tag_id = #{tagId}


        AND b.user_id = #{userId}


ORDER BY b.create_time DESC

mapper.update:
UPDATE blog
title = #{title}, content = #{content}, summary = #{summary}, cover_image = #{coverImage}, category_id = #{categoryId}, status = #{status}, update_time = #{updateTime},
WHERE id = #{id}
