mapper.select:
SELECT id, user_id, blog_id, create_time 
FROM favorite 
WHERE user_id = #{userId} AND blog_id = #{blogId}

SELECT DISTINCT f.blog_id 
FROM favorite f

    INNER JOIN blog_tag bt ON f.blog_id = bt.blog_id

WHERE f.user_id = #{userId}

    AND bt.tag_id = #{tagId}

ORDER BY f.create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(DISTINCT f.blog_id) 
FROM favorite f

    INNER JOIN blog_tag bt ON f.blog_id = bt.blog_id

WHERE f.user_id = #{userId}

    AND bt.tag_id = #{tagId}

SELECT f.blog_id 
FROM favorite f
WHERE f.user_id = #{userId}

    AND f.tag_name = #{tagName}

ORDER BY f.create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) 
FROM favorite f
WHERE f.user_id = #{userId}

    AND f.tag_name = #{tagName}
